import React, { useEffect, useState } from 'react';
import styles from './movieList.module.css'; // Import the CSS file

function MovieList({ category, fetchUrl, isOriginals, onClick }) {
    const [movies, setMovies] = useState([]);

    // Fetch movies based on the provided fetchUrl
    useEffect(() => {
        async function fetchData() {
            const response = await fetch(fetchUrl);
            const data = await response.json();
            setMovies(data.results);
        }
        fetchData();

    }, [fetchUrl]);

    const moviesToDisplay = movies.length > 0 ? movies : fetchedMovies;

    return (
        <div className={styles.group}>
            <h2>{category}</h2>
            <div className={styles.movieContainer}>
                {movies.map((movie) => (
                    <div
                        key={movie.id}
                        className={`${styles.movieItem} ${isOriginals ? styles.netflixOriginals : styles.otherGenres}`}
                        onClick={() => onClick(movie, movies)}  // Pass the movie and the full list for the genre
                    >
                        {/* Display poster for Netflix Originals, and backdrop for other genres */}
                        <img
                            src={`https://image.tmdb.org/t/p/w500${isOriginals ? movie.poster_path : movie.backdrop_path}`}
                            alt={movie.title || movie.name}
                            className={styles.movieImage}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default MovieList;
