import React, { useEffect, useState } from 'react';
import styles from './movieList.module.css'; // Import the CSS file

function MovieList({ category, fetchUrl, isOriginals, onClick }) {
    const [movies, setMovies] = useState([]);
    const API_BASE_URL = 'https://api.themoviedb.org/3';


    useEffect(() => {
        if (fetchUrl) {
            async function fetchData() {
                const response = await fetch(`${API_BASE_URL}${fetchUrl}`);
                const data = await response.json();
                setMovies(data.results);

            }
            fetchData();
        }
    }, [fetchUrl]);

    // Use either the provided `movies` prop or the fetched movies
    const moviesToDisplay = movies.length > 0 ? movies : fetchedMovies;

    return (
        <div className={styles.group}>
            <h2>{category}</h2>
            <div className={styles.movieContainer}>
                {moviesToDisplay.map((movie) => (
                    <div
                        key={movie.id}
                        className={`${styles.movieItem} ${isOriginals ? styles.netflixOriginals : styles.otherGenres}`}
                        onClick={() => onClick(movie, movies)}  // Pass the movie click event to parent
                    >
                        {/* Conditional rendering: show poster for Netflix Originals, backdrop for other genres */}
                        <img
                            src={`https://image.tmdb.org/t/p/w500${isOriginals ? movie.poster_path : movie.backdrop_path}`}
                            alt={movie.title}
                            className={styles.movieImage}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default MovieList;
