import React, { useState } from 'react';
import styles from './movie.module.css';  // Import the CSS file
import MovieList from './pages/MovieDetail/MovieList';

// Base URL for all API requests
// const API_BASE_URL = 'https://api.themoviedb.org/3';

// API key
const API_KEY = 'a090b75e1744dd94d481ba67ad26903e';

const requests = {
  fetchTrending: `/trending/all/week?api_key=${API_KEY}&language=en-US`,
  fetchNetflixOriginals: `/discover/tv?api_key=${API_KEY}&with_network=123`,
  fetchTopRated: `/movie/top_rated?api_key=${API_KEY}&language=en-US`,
  fetchActionMovies: `/discover/movie?api_key=${API_KEY}&with_genres=28`,
  fetchComedyMovies: `/discover/movie?api_key=${API_KEY}&with_genres=35`,
  fetchHorrorMovies: `/discover/movie?api_key=${API_KEY}&with_genres=27`,
  fetchRomanceMovies: `/discover/movie?api_key=${API_KEY}&with_genres=10749`,
  fetchDocumentaries: `/discover/movie?api_key=${API_KEY}&with_genres=99`,
  fetchSearch: `/search/movie?api_key=${API_KEY}&language=en-US`
};


function Movie() {
  const [selectedMovie, setSelectedMovie] = useState(null);   // Track the selected movie
  const [sameGenreMovies, setSameGenreMovies] = useState([]); // Movies in the same genre as the selected movie
  const [isMovieSelected, setIsMovieSelected] = useState(false); // If a movie is selected



  // Function to handle when a movie is clicked
  const handleMovieClick = (movie, moviesFromSameUrl) => {
    if (selectedMovie && selectedMovie.id === movie.id) {
      // Deselect the movie
      setSelectedMovie(null);
      setIsMovieSelected(false);
    } else {
      // Select the movie and filter same genre movies
      setSelectedMovie(movie);
      setIsMovieSelected(true);
      const genreMovies = moviesInCategory.filter(m => m.id !== movie.id && m.genre_ids.includes(movie.genre_ids[0]));
      setSameGenreMovies(genreMovies);
    }
  }

  return (

    <div className={styles.group}>
      {isMovieSelected ? (
        <>
          {/* Display same genre movies and movie details */}
          <div className={styles.sameGenreMovies}>
            <h2>Movies in the Same Genre</h2>
            <MovieList
              movies={sameGenreMovies}
              onClick={handleMovieClick}
            />
          </div>

          {/* Display selected movie details */}
          {selectedMovie && (
            <div className={styles.movieDetails}>
              <h2>{selectedMovie.title || selectedMovie.name}</h2>
              <p><strong>Release Date:</strong> {selectedMovie.first_air_date || selectedMovie.first_air_date}</p>
              <p><strong>Rating:</strong> {selectedMovie.vote_average}/10</p>
              <p><strong>Overview:</strong> {selectedMovie.overview}</p>
            </div>
          )}
        </>
      ) : (
        <>
          {/* Render MovieLists for different categories */}
          <MovieList category="" fetchUrl={requests.fetchNetflixOriginals} isOriginals={true} onClick={handleMovieClick} />
          <MovieList category="Xu hướng" fetchUrl={requests.fetchTrending} isOriginals={false} onClick={handleMovieClick} />
          <MovieList category="Xếp hạng cao" fetchUrl={requests.fetchTopRated} isOriginals={false} onClick={handleMovieClick} />
          <MovieList category="Hành động" fetchUrl={requests.fetchActionMovies} isOriginals={false} onClick={handleMovieClick} />
          <MovieList category="Hài" fetchUrl={requests.fetchComedyMovies} isOriginals={false} onClick={handleMovieClick} />
          <MovieList category="Kinh dị" fetchUrl={requests.fetchHorrorMovies} isOriginals={false} onClick={handleMovieClick} />
          <MovieList category="Lãng mạn" fetchUrl={requests.fetchRomanceMovies} isOriginals={false} onClick={handleMovieClick} />
          <MovieList category="Tài liệu" fetchUrl={requests.fetchDocumentaries} isOriginals={false} onClick={handleMovieClick} />
        </>
      )}
    </div>
  );
}

export default Movie;
