// API Key
// a090b75e1744dd94d481ba67ad26903e

// API Read Access Token
// eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiJhMDkwYjc1ZTE3NDRkZDk0ZDQ4MWJhNjdhZDI2OTAzZSIsIm5iZiI6MTcyODMxNzQxNy40MDUxOTEsInN1YiI6IjY3MDQwNTk3M2FmYzkxYTVmMTEzYTM3NSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.ZSwPF56KWx_fo1dbaMRBXZqrR-hFstpP4L2owhxKZng


import React, { useState, useEffect } from 'react';

// Base URL for all API requests
const API_BASE_URL = 'https://api.themoviedb.org/3';

// API key
const API_KEY = 'a090b75e1744dd94d481ba67ad26903e';

const requests = {
  fetchTrending: `/trending/all/week?api_key=${API_KEY}&language=en-US`,
  fetchNetflixOriginals: `/discover/tv?api_key=${API_KEY}&with_network=123`,
  fetchTopRated: `/movie/top_rated?api_key=${API_KEY}&language=en-US`,
  fetchActionMovies: `/discover/movie?api_key=${API_KEY}&with_genres=28`,
  fetchComedyMovies: `/discover/movie?api_key=${API_KEY}&with_genres=35`,
  fetchHorrorMovies: `/discover/movie?api_key=${API_KEY}&with_genres=27`,
  fetchRomanceMovies: `/discover/movie?api_key=${API_KEY}&with_genres=10749`,
  fetchDocumentaries: `/discover/movie?api_key=${API_KEY}&with_genres=99`,
  fetchSearch: `/search/movie?api_key=${API_KEY}&language=en-US`,
};

function MovieList({ category, fetchUrl }) {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await fetch(`${API_BASE_URL}${fetchUrl}`);
      const data = await response.json();
      setMovies(data.results);
    }

    fetchData();
  }, [fetchUrl]);

  return (
    <div>
      <h2>{category}</h2>
      <div style={{ display: 'flex', overflowX: 'scroll' }}>
        {movies.map((movie) => (
          <div key={movie.id} style={{ margin: '0 10px' }}>
            <img
              src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
              alt={movie.title || movie.name}
              style={{ width: '150px', borderRadius: '5px' }}
            />
            <p>{movie.title || movie.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

function App() {
  return (
    <div>
      <MovieList category="Trending" fetchUrl={requests.fetchTrending} />
      <MovieList category="Top Rated" fetchUrl={requests.fetchTopRated} />
      <MovieList category="Action Movies" fetchUrl={requests.fetchActionMovies} />
      <MovieList category="Comedy Movies" fetchUrl={requests.fetchComedyMovies} />
      <MovieList category="Horror Movies" fetchUrl={requests.fetchHorrorMovies} />
      <MovieList category="Romance Movies" fetchUrl={requests.fetchRomanceMovies} />
      <MovieList category="Documentaries" fetchUrl={requests.fetchDocumentaries} />
    </div>
  );
}

export default Movie;
